    private void checkActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_checkActionPerformed
        // TODO add your handling code here:
        //type = Int.parseString(spending.getText());
        type = Integer.parseInt(spending.getText());
        
        switch(type)
        {
            case 1 :
                JOptionPane.showMessageDialog(null,"Keep it up. Keep in mind you might need to spend on essentials like clothing and groceries");
                break;
            case 2 :
                JOptionPane.showMessageDialog(null,"Consider investing your money. You'll be part owner of a company while getting money from them!");
                JOptionPane.showMessageDialog(null,"If you have children, consider setting aside some money for their welfare and education.");
                break;
            case 3 :
                JOptionPane.showMessageDialog(null,"Look at the categories you spend most for. Are these really important?");
                JOptionPane.showMessageDialog(null,"Consider applying for a rewards card. You'll get money back, which accumulates.");
                break;
        }
        
        spending.setText(null);
    }//GEN-LAST:event_checkActionPerformed

